import { coerceBooleanProperty } from '@angular/cdk/coercion';
import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  Input,
  OnDestroy,
  OnInit,
  Optional,
  ViewEncapsulation,
  Inject,
  ElementRef,
  IterableDiffers,
  NgZone,
} from '@angular/core';
import { CanDisable, CanDisableCtor, mixinDisabled } from '../core';
import { merge, Subscription } from 'rxjs';
import { OuiSort, OuiSortable } from './sort';
import { ouiSortAnimations } from './sort-animations';
import { SortDirection } from './sort-direction';
import { getSortHeaderNotContainedWithinSortError } from './sort-errors';
import { OuiSortHeaderIntl } from './sort-header-intl';
import { FocusMonitor } from '@angular/cdk/a11y';

// Boilerplate for applying mixins to the sort header.
/** @docs-private */
export class OuiSortHeaderBase {}
export const _OuiSortHeaderMixinBase: CanDisableCtor &
  typeof OuiSortHeaderBase = mixinDisabled(OuiSortHeaderBase);

/**
 * Valid positions for the arrow to be in for its opacity and translation. If the state is a
 * sort direction, the position of the arrow will be above/below and opacity 0. If the state is
 * hint, the arrow will be in the center with a slight opacity. Active state means the arrow will
 * be fully opaque in the center.
 *
 * @docs-private
 */
export type ArrowViewState = SortDirection | 'hint' | 'active';

/**
 * States describing the arrow's animated position (animating fromState to toState).
 * If the fromState is not defined, there will be no animated transition to the toState.
 *
 * @docs-private
 */
export interface ArrowViewStateTransition {
  fromState?: ArrowViewState;
  toState: ArrowViewState;
}

/** Column definition associated with a `OuiSortHeader`. */
interface OuiSortHeaderColumnDef {
  name: string;
}

/**
 * Applies sorting behavior (click to change sort) and styles to an element, including an
 * arrow to display the current sort direction.
 *
 * Must be provided with an id and contained within a parent OuiSort directive.
 *
 * If used on header cells in a CdkTable, it will automatically default its id from its containing
 * column definition.
 */
@Component({
  // eslint-disable-next-line
  selector: '[oui-sort-header]',
  exportAs: 'ouiSortHeader',
  templateUrl: 'sort-header.html',
  styleUrls: ['sort-header.scss'],
  // eslint-disable-next-line @angular-eslint/no-host-metadata-property
  host: {
    '(click)': '_handleClick()',
    '(mouseenter)': '_setIndicatorHintVisible(true)',
    '(longpress)': '_setIndicatorHintVisible(true)',
    '(mouseleave)': '_setIndicatorHintVisible(false)',
    '[attr.aria-sort]': '_getAriaSortAttribute()',
    '[class.oui-sort-header-disabled]': '_isDisabled()',
  },
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush,
  // eslint-disable-next-line @angular-eslint/no-inputs-metadata-property
  inputs: ['disabled'],
  animations: [
    ouiSortAnimations.indicator,
    ouiSortAnimations.leftPointer,
    ouiSortAnimations.rightPointer,
    ouiSortAnimations.arrowOpacity,
    ouiSortAnimations.arrowPosition,
    ouiSortAnimations.allowChildren,
  ],
})
export class OuiSortHeader
  extends _OuiSortHeaderMixinBase
  implements CanDisable, OuiSortable, OnDestroy, OnInit
{
  private _rerenderSubscription: Subscription;

  /**
   * Flag set to true when the indicator should be displayed while the sort is not active. Used to
   * provide an affordance that the header is sortable by showing on focus and hover.
   */
  _showIndicatorHint = false;

  /**
   * The view transition state of the arrow (translation/ opacity) - indicates its `from` and `to`
   * position through the animation. If animations are currently disabled, the fromState is removed
   * so that there is no animation displayed.
   */
  _viewState: ArrowViewStateTransition;

  /** The direction the arrow should be facing according to the current state. */
  _arrowDirection: SortDirection = '';

  /**
   * Whether the view state animation should show the transition between the `from` and `to` states.
   */
  _disableViewStateAnimation = false;

  /**
   * ID of this sort header. If used within the context of a CdkColumnDef, this will default to
   * the column's name.
   */
  // eslint-disable-next-line @angular-eslint/no-input-rename
  @Input('oui-sort-header') id: string;

  /** Sets the position of the arrow that displays when sorted. */
  @Input() arrowPosition: 'before' | 'after' = 'after';

  /** Overrides the sort start value of the containing OuiSort for this OuiSortable. */
  @Input() start: 'asc' | 'desc';

  // To set browser tooltip
  title: string;

  /** Overrides the disable clear value of the containing OuiSort for this OuiSortable. */
  @Input()
  get disableClear(): boolean {
    return this._disableClear;
  }
  set disableClear(v) {
    this._disableClear = coerceBooleanProperty(v);
  }
  private _disableClear: boolean;
  private _monitorSubscription: Subscription = Subscription.EMPTY;
  constructor(
    public _intl: OuiSortHeaderIntl,
    changeDetectorRef: ChangeDetectorRef,
    protected elementRef: ElementRef<HTMLElement>,
    protected _differs: IterableDiffers,
    private _focusMonitor: FocusMonitor,
    private _ngZone: NgZone,
    @Optional() public _sort: OuiSort,
    @Inject('OUI_SORT_HEADER_COLUMN_DEF')
    @Optional()
    public _columnDef: OuiSortHeaderColumnDef,
    private _elementRef: ElementRef
  ) {
    // Note that we use a string token for the `_columnDef`, because the value is provided both by
    // `once-ui/table` and `cdk/table` and we can't have the CDK depending on once-ui,
    // and we want to avoid having the sort header depending on the CDK table because
    // of this single reference.
    super();
    this._monitorSubscription = this._focusMonitor
      .monitor(this.elementRef, true)
      .subscribe(() => this._ngZone.run(() => {}));

    if (!_sort) {
      throw getSortHeaderNotContainedWithinSortError();
    }

    this._rerenderSubscription = merge(
      _sort.sortChange,
      _sort._stateChanges,
      _intl.changes
    ).subscribe(() => {
      if (this._isSorted()) {
        this._updateArrowDirection();
      }

      // If this header was recently active and now no longer sorted, animate away the arrow.
      if (
        !this._isSorted() &&
        this._viewState &&
        this._viewState.toState === 'active'
      ) {
        this._disableViewStateAnimation = false;
        this._setAnimationTransitionState({
          fromState: 'active',
          toState: this._arrowDirection,
        });
      }

      changeDetectorRef.markForCheck();
    });
  }

  ngOnInit() {
    const columnHeading: string = this._elementRef.nativeElement.innerText;
    this.title = 'Sort by ' + columnHeading;
    if (!this.id && this._columnDef) {
      this.id = this._columnDef.name;
    }

    // Initialize the direction of the arrow and set the view state to be immediately that state.
    this._updateArrowDirection();
    this._setAnimationTransitionState({
      toState: this._isSorted() ? 'active' : this._arrowDirection,
    });

    this._sort.register(this);
  }

  ngOnDestroy() {
    this._sort.deregister(this);
    this._rerenderSubscription.unsubscribe();
    this._focusMonitor.stopMonitoring(this.elementRef);
    this._monitorSubscription.unsubscribe();
  }

  /**
   * Sets the "hint" state such that the arrow will be semi-transparently displayed as a hint to the
   * user showing what the active sort will become. If set to false, the arrow will fade away.
   */
  _setIndicatorHintVisible(visible: boolean) {
    // No-op if the sort header is disabled - should not make the hint visible.
    if (this._isDisabled() && visible) {
      return;
    }

    this._showIndicatorHint = visible;

    if (!this._isSorted()) {
      this._updateArrowDirection();
      if (this._showIndicatorHint) {
        this._setAnimationTransitionState({
          fromState: this._arrowDirection,
          toState: 'hint',
        });
      } else {
        this._setAnimationTransitionState({
          fromState: 'hint',
          toState: this._arrowDirection,
        });
      }
    }
  }

  /**
   * Sets the animation transition view state for the arrow's position and opacity. If the
   * `disableViewStateAnimation` flag is set to true, the `fromState` will be ignored so that
   * no animation appears.
   */
  _setAnimationTransitionState(viewState: ArrowViewStateTransition) {
    this._viewState = viewState;

    // If the animation for arrow position state (opacity/translation) should be disabled,
    // remove the fromState so that it jumps right to the toState.
    if (this._disableViewStateAnimation) {
      this._viewState = { toState: viewState.toState };
    }
  }

  /** Triggers the sort on this sort header and removes the indicator hint. */
  _handleClick() {
    if (this._isDisabled()) {
      return;
    }

    this._sort.sort(this);

    // Do not show the animation if the header was already shown in the right position.
    if (
      this._viewState.toState === 'hint' ||
      this._viewState.toState === 'active'
    ) {
      this._disableViewStateAnimation = true;
    }

    // If the arrow is now sorted, animate the arrow into place. Otherwise, animate it away into
    // the direction it is facing.
    const viewState: ArrowViewStateTransition = this._isSorted()
      ? { fromState: this._arrowDirection, toState: 'active' }
      : { fromState: 'active', toState: this._arrowDirection };
    this._setAnimationTransitionState(viewState);

    this._showIndicatorHint = false;
  }

  /** Whether this OuiSortHeader is currently sorted in either ascending or descending order. */
  _isSorted() {
    return (
      this._sort.active === this.id &&
      (this._sort.direction === 'asc' || this._sort.direction === 'desc')
    );
  }

  /** Returns the animation state for the arrow direction (indicator and pointers). */
  _getArrowDirectionState() {
    return `${this._isSorted() ? 'active-' : ''}${this._arrowDirection}`;
  }

  /** Returns the arrow position state (opacity, translation). */
  _getArrowViewState() {
    const fromState = this._viewState.fromState;
    return (fromState ? `${fromState}-to-` : '') + this._viewState.toState;
  }

  /**
   * Updates the direction the arrow should be pointing. If it is not sorted, the arrow should be
   * facing the start direction. Otherwise if it is sorted, the arrow should point in the currently
   * active sorted direction. The reason this is updated through a function is because the direction
   * should only be changed at specific times - when deactivated but the hint is displayed and when
   * the sort is active and the direction changes. Otherwise the arrow's direction should linger
   * in cases such as the sort becoming deactivated but we want to animate the arrow away while
   * preserving its direction, even though the next sort direction is actually different and should
   * only be changed once the arrow displays again (hint or activation).
   */
  _updateArrowDirection() {
    this._arrowDirection = this._isSorted()
      ? this._sort.direction
      : this.start || this._sort.start;
  }

  _isDisabled() {
    return this._sort.disabled || this.disabled;
  }

  /**
   * Gets the aria-sort attribute that should be applied to this sort header. If this header
   * is not sorted, returns null so that the attribute is removed from the host element. Aria spec
   * says that the aria-sort property should only be present on one header at a time, so removing
   * ensures this is true.
   */
  _getAriaSortAttribute() {
    if (!this._isSorted()) {
      return null;
    }

    return this._sort.direction === 'asc' ? 'ascending' : 'descending';
  }
}

import { Constructor } from './constructor';
import { ElementRef } from '@angular/core';

/** @docs-private */
export interface CanColor {
  /** Theme color palette for the component. */
  color: ThemePalette;
}

/** Possible color palette values. */
export type ThemePalette = 'primary' | 'accent' | 'warn' | undefined;

/** @docs-private */
export interface HasElementRef {
  _elementRef: ElementRef;
}

/** @docs-private */
export type CanColorCtor = Constructor<CanColor>;

/** Mixin to augment a directive with a `color` property. */
export function mixinColor<T extends Constructor<HasElementRef>>(
  base: T,
  defaultColor?: ThemePalette
): CanColorCtor & T {
  return class extends base {
    private _color: ThemePalette;

    get color(): ThemePalette {
      return this._color;
    }
    set color(value: ThemePalette) {
      const colorPalette = value || defaultColor;

      if (colorPalette !== this._color) {
        if (this._color) {
          this._elementRef.nativeElement.classList.remove(`oui-${this._color}`);
        }
        if (colorPalette) {
          this._elementRef.nativeElement.classList.add(`oui-${colorPalette}`);
        }

        this._color = colorPalette;
      }
    }

    constructor(...args: any[]) {
      // eslint-disable-next-line @typescript-eslint/no-unsafe-argument
      super(...args);
      // Set the default color that can be specified from the mixin.
      this.color = defaultColor;
    }
  };
}

import { coerceBooleanProperty } from '@angular/cdk/coercion';
import { Constructor } from './constructor';

export interface CanDisable {
  /** Whether the component is disabled. */
  disabled: boolean;
}

/** @docs-private */
export type CanDisableCtor = Constructor<CanDisable>;

/** Mixin to augment a directive with a `disabled` property. */
export function mixinDisabled<T extends Constructor<{}>>(
  base: T
): CanDisableCtor & T {
  return class extends base {
    private _disabled = false;

    get disabled() {
      return this._disabled;
    }
    set disabled(value: any) {
      this._disabled = coerceBooleanProperty(value);
    }

    constructor(...args: any[]) {
      // eslint-disable-next-line @typescript-eslint/no-unsafe-argument
      super(...args);
    }
  };
}

import { Constructor } from '../core/common-behaviors/constructor';
import { ElementRef, ChangeDetectorRef } from '@angular/core';

/** @docs-private */
export interface CanProgress {
  /** progress texts */
  progress: string[] | string;
  setToProgress: Function;
  setToDone: Function;
  setToDefault: Function;
}

/** @docs-private */
export interface HasElementRef {
  _elementRef: ElementRef;
}

/** @docs-private */
export type CanProgressCtor = Constructor<CanProgress>;

/** Mixin to augment a directive with a `color` property. */
export function mixinProgress<T extends Constructor<HasElementRef>>(
  base: T
): CanProgressCtor & T {
  return class extends base {
    private _progress: string[] | string;
    private _cdr: ChangeDetectorRef;
    private _stage: 'default' | 'progress' | 'done' = 'default';
    get progress(): string[] | string {
      return this._progress;
    }
    set progress(value: string[] | string) {
      if (value === '') {
        this._progress = ['Save', 'Saving...', 'Saved'];
      } else {
        this._progress = value;
      }
      setTimeout(() => {
        this._changeStage();
        this._cdr.detectChanges();
      }, 0);
    }

    constructor(...args: any[]) {
      // eslint-disable-next-line @typescript-eslint/no-unsafe-argument
      super(...args);
      this._cdr = args[1];
      this._progress = null;
    }

    private _setButtonText(text: string) {
      this._elementRef.nativeElement.children[0].innerHTML = text;
    }

    private _addClass(className: string) {
      this._elementRef.nativeElement.classList.add(className);
    }

    private _checkAttribute() {
      if (this._elementRef.nativeElement.tagName === 'A') {
        throw Error('please use <button> tag to use progress button');
      }
      if (!this._progress) {
        throw Error(
          'please add progress input attribute to change button state'
        );
      }
    }

    private _changeStage() {
      if (!this._progress) {
        return;
      }
      const indexes = { default: 0, progress: 1, done: 2 };
      const labelIndex = indexes[this._stage];
      this._setButtonText(this._progress[labelIndex]);
      this._removeClasses();
      this._addClass(`oui-stage-${this._stage}`);
    }

    private _removeClasses() {
      const stages = ['default', 'progress', 'done'];
      for (const stage of stages) {
        this._elementRef.nativeElement.classList.remove(`oui-stage-${stage}`);
      }
    }

    setToDefault() {
      this._checkAttribute();
      this._elementRef.nativeElement.disabled = false;
      this._stage = 'default';
      this._changeStage();
    }

    setToProgress() {
      this._checkAttribute();
      this._elementRef.nativeElement.disabled = true;
      this._stage = 'progress';
      this._changeStage();
    }

    setToDone() {
      this._checkAttribute();
      this._elementRef.nativeElement.disabled = false;
      this._stage = 'done';
      this._changeStage();
      this._resetToDefault();
    }

    private _resetToDefault() {
      setTimeout(() => {
        this._stage = 'default';
        this._changeStage();
      }, 3000);
    }
  };
}

import {
  UntypedFormControl,
  FormGroupDirective,
  NgControl,
  NgForm,
} from '@angular/forms';
import { Subject } from 'rxjs';
import { ErrorStateMatcher } from './error-options';
import { Constructor } from './constructor';

/** @docs-private */
export interface CanUpdateErrorState {
  readonly stateChanges: Subject<void>;
  errorState: boolean;
  errorStateMatcher: ErrorStateMatcher;
  updateErrorState(): void;
}

/** @docs-private */
export type CanUpdateErrorStateCtor = Constructor<CanUpdateErrorState>;

/** @docs-private */
export interface HasErrorState {
  _parentFormGroup: FormGroupDirective;
  _parentForm: NgForm;
  _defaultErrorStateMatcher: ErrorStateMatcher;
  ngControl: NgControl;
}

/**
 * Mixin to augment a directive with updateErrorState method.
 * For component with `errorState` and need to update `errorState`.
 */
export function mixinErrorState<T extends Constructor<HasErrorState>>(
  base: T
): CanUpdateErrorStateCtor & T {
  return class extends base {
    /** Whether the component is in an error state. */
    errorState = false;

    /**
     * Stream that emits whenever the state of the input changes such that the wrapping
     * `MatFormField` needs to run change detection.
     */
    readonly stateChanges = new Subject<void>();

    errorStateMatcher: ErrorStateMatcher;

    updateErrorState() {
      const oldState = this.errorState;
      const parent = this._parentFormGroup || this._parentForm;
      const matcher = this.errorStateMatcher || this._defaultErrorStateMatcher;
      const control = this.ngControl
        ? (this.ngControl.control as UntypedFormControl)
        : null;
      const newState = matcher.isErrorState(control, parent);

      if (newState !== oldState) {
        this.errorState = newState;
        this.stateChanges.next();
      }
    }

    constructor(...args: any[]) {
      // eslint-disable-next-line @typescript-eslint/no-unsafe-argument
      super(...args);
    }
  };
}

import { ViewContainerRef } from '@angular/core';
import { Direction } from '@angular/cdk/bidi';
import { ScrollStrategy } from '@angular/cdk/overlay';

/** Possible overrides for a dialog's position. */
export interface DialogPosition {
  /** Override for the dialog's top position. */
  top?: string;

  /** Override for the dialog's bottom position. */
  bottom?: string;

  /** Override for the dialog's left position. */
  left?: string;

  /** Override for the dialog's right position. */
  right?: string;
}

/**
 * Configuration for opening a modal dialog with the OuiDialog service.
 */
export class OuiDialogConfig<D = any> {
  /**
   * Where the attached component should live in Angular's *logical* component tree.
   * This affects what is available for injection and the change detection order for the
   * component instantiated inside of the dialog. This does not affect where the dialog
   * content will be rendered.
   */
  viewContainerRef?: ViewContainerRef;

  /** ID for the dialog. If omitted, a unique one will be generated. */
  id?: string;

  /** The ARIA role of the dialog element. */
  role? = 'dialog';

  /** Custom class for the overlay pane. */
  panelClass?: string | string[] = '';

  /** Whether the dialog has a backdrop. */
  hasBackdrop? = true;

  /** Custom class for the backdrop, */
  backdropClass? = '';

  /** Whether the user can use escape or clicking on the backdrop to close the modal. */
  disableClose? = false;

  /** Width of the dialog. */
  width? = '607px';

  /** Height of the dialog. */
  height? = '';

  /** Min-width of the dialog. If a number is provided, pixel units are assumed. */
  minWidth?: number | string = '607px';

  /** Min-height of the dialog. If a number is provided, pixel units are assumed. */
  minHeight?: number | string;

  /** Max-width of the dialog. If a number is provided, pixel units are assumed. Defaults to 80vw */
  maxWidth?: number | string = '80vw';

  /** Max-height of the dialog. If a number is provided, pixel units are assumed. */
  maxHeight?: number | string;

  /** Position overrides. */
  position?: DialogPosition;

  /** Data being injected into the child component. */
  data?: D | null = null;

  /** Layout direction for the dialog's content. */
  direction?: Direction;

  /** ID of the element that describes the dialog. */
  ariaDescribedBy?: string | null = null;

  /** Aria label to assign to the dialog element */
  ariaLabel?: string | null = null;

  /** Whether the dialog should focus the first focusable element on open. */
  autoFocus? = false;

  /**
   * Whether the dialog should restore focus to the
   * previously-focused element, after it's closed.
   */
  restoreFocus? = true;

  /** Scroll strategy to be used for the dialog. */
  scrollStrategy?: ScrollStrategy;

  // TODO(jelbourn): add configuration for lifecycle hooks, ARIA labelling.
}

