    // first tap from firing its click event or can cause the tooltip to open for clicks.
    if (!platform.IOS && !platform.ANDROID) {
      this._manualListeners
        .set('mouseenter', () => this.show())
        .set('mouseleave', () => this.hide());
    } else if (!hasGestures) {
      // there's no way for the user to trigger the tooltip on a touch device.
      this._manualListeners.set('touchstart', () => this.show());
    }

    this._manualListeners.forEach((listener, event) =>
      element.addEventListener(event, listener)
    );

    if (element.nodeName === 'INPUT' || element.nodeName === 'TEXTAREA') {
      elementStyle.webkitUserSelect =
        elementStyle.userSelect =
        elementStyle.msUserSelect =
          '';
    }

    // Hammer applies `-webkit-user-drag: none` on all elements by default,
    // which breaks the native drag&drop. If the consumer explicitly made
    // the element draggable, clear the `-webkit-user-drag`.
    if (element.draggable && elementStyle.webkitUserDrag === 'none') {
      elementStyle.webkitUserDrag = '';
    }

    _focusMonitor
      .monitor(_elementRef)
      .pipe(takeUntil(this._destroyed))
      .subscribe((origin) => {
        // Note that the focus monitor runs outside the Angular zone.
        if (!origin) {
          _ngZone.run(() => this.hide());
        } else if (origin === 'keyboard') {
          _ngZone.run(() => this.show());
        }
      });
  }

  /**
   * Dispose the tooltip when destroyed.
   */
  ngOnDestroy() {
    if (this._overlayRef) {
      this._overlayRef.dispose();
      this._tooltipInstance = null;
    }

    // Clean up the event listeners set in the constructor
    this._manualListeners.forEach((listener, event) => {
      this._elementRef.nativeElement.removeEventListener(event, listener);
    });
    this._manualListeners.clear();

    this._destroyed.next();
    this._destroyed.complete();

    this._ariaDescriber.removeDescription(
      this._elementRef.nativeElement,
      this.message
    );
    this._focusMonitor.stopMonitoring(this._elementRef);
  }

  /** Shows the tooltip after the delay in ms, defaults to tooltip-delay-show or 0ms if no input */
  show(): void {
    if (
      this.disabled ||
      !this.message ||
      (this._isTooltipVisible() &&
        !this._tooltipInstance!._showTimeoutId &&
        !this._tooltipInstance!._hideTimeoutId)
    ) {
      return;
    }

    const overlayRef = this._createOverlay();

    this._detach();
    this._portal =
      this._portal ||
      new ComponentPortal(TooltipComponent, this._viewContainerRef);
    this._tooltipInstance = overlayRef.attach(this._portal).instance;
    this._tooltipInstance
      .afterHidden()
      .pipe(takeUntil(this._destroyed))
      .subscribe(() => this._detach());
    this._setTooltipClass(this._tooltipClass);
    this._updateTooltipMessage();
    this._tooltipInstance!.show();
  }

  /** Hides the tooltip after the delay in ms, defaults to tooltip-delay-hide or 0ms if no input */
  hide(): void {
    if (this._tooltipInstance) {
      this._tooltipInstance.hide();
    }
  }

  /** Shows/hides the tooltip */
  toggle(): void {
    this._isTooltipVisible() ? this.hide() : this.show();
  }

  /** Returns true if the tooltip is currently visible to the user */
  _isTooltipVisible(): boolean {
    return !!this._tooltipInstance && this._tooltipInstance.isVisible();
  }

  /** Handles the keydown events on the host element. */
  _handleKeydown(e: KeyboardEvent) {
    if (this._isTooltipVisible() && e.key === 'Escape') {
      e.stopPropagation();
      this.hide();
    }
  }

  /** Handles the touchend events on the host element. */
  _handleTouchend() {
    this.hide();
  }

  /** Create the overlay config and position strategy */
  private _createOverlay(): OverlayRef {
    if (this._overlayRef) {
      return this._overlayRef;
    }

    // Create connected position strategy that listens for scroll events to reposition.
    const strategy = this._overlay
      .position()
      .flexibleConnectedTo(this._elementRef)
      .withTransformOriginOn('.oui-tooltip')
      .withFlexibleDimensions(false)
      .withViewportMargin(8);

    const scrollableAncestors =
      this._scrollDispatcher.getAncestorScrollContainers(this._elementRef);

    strategy.withScrollableContainers(scrollableAncestors);

    strategy.positionChanges
      .pipe(takeUntil(this._destroyed))
      .subscribe((change) => {
        if (this._tooltipInstance) {
          if (
            change.scrollableViewProperties.isOverlayClipped &&
            this._tooltipInstance.isVisible()
          ) {
            // After position changes occur and the overlay is clipped by
            // a parent scrollable then close the tooltip.
            this._ngZone.run(() => this.hide());
          }
        }
      });

    this._overlayRef = this._overlay.create({
      direction: this._dir,
      positionStrategy: strategy,
      panelClass: TOOLTIP_PANEL_CLASS,
      scrollStrategy: this._scrollStrategy(),
    });

    this._updatePosition();

    this._overlayRef
      .detachments()
      .pipe(takeUntil(this._destroyed))
      .subscribe(() => this._detach());

    return this._overlayRef;
  }

  /** Detaches the currently-attached tooltip. */
  private _detach() {
    if (this._overlayRef && this._overlayRef.hasAttached()) {
      this._overlayRef.detach();
    }

    this._tooltipInstance = null;
  }

  /** Updates the position of the current tooltip. */
  private _updatePosition() {
    const position = this._overlayRef!.getConfig()
      .positionStrategy as FlexibleConnectedPositionStrategy;
    const origin = this._getOrigin();
    const overlay = this._getOverlayPosition();

    position.withPositions([
      { ...origin.main, ...overlay.main },
      { ...origin.fallback, ...overlay.fallback },
    ]);
  }

  /**
   * Returns the origin position and a fallback position based on the user's position preference.
   * The fallback position is the inverse of the origin (e.g. `'below' -> 'above'`).
   */
  _getOrigin(): {
    main: OriginConnectionPosition;
    fallback: OriginConnectionPosition;
  } {
    const isLtr = !this._dir || this._dir.value === 'ltr';
    const position = this.position;
    let originPosition: OriginConnectionPosition;

    if (position === 'above' || position === 'below') {
      originPosition = {
        originX: 'center',
        originY: position === 'above' ? 'top' : 'bottom',
      };
    } else if (
      (position === 'left' && isLtr) ||
      (position === 'right' && !isLtr)
    ) {
      originPosition = { originX: 'start', originY: 'center' };
    } else if (
      (position === 'right' && isLtr) ||
      (position === 'left' && !isLtr)
    ) {
      originPosition = { originX: 'end', originY: 'center' };
    } else {
      throw getOuiTooltipInvalidPositionError(position);
    }

    const { x, y } = this._invertPosition(
      originPosition.originX,
      originPosition.originY
    );

    return {
      main: originPosition,
      fallback: { originX: x, originY: y },
    };
  }

  /** Returns the overlay position and a fallback position based on the user's preference */
  _getOverlayPosition(): {
    main: OverlayConnectionPosition;
    fallback: OverlayConnectionPosition;
  } {
    const isLtr = !this._dir || this._dir.value === 'ltr';
    const position = this.position;
    let overlayPosition: OverlayConnectionPosition;

    if (position === 'above') {
      overlayPosition = { overlayX: 'center', overlayY: 'bottom' };
    } else if (position === 'below') {
      overlayPosition = { overlayX: 'center', overlayY: 'top' };
    } else if (
      (position === 'left' && isLtr) ||
      (position === 'right' && !isLtr)
    ) {
      overlayPosition = { overlayX: 'end', overlayY: 'center' };
    } else if (
      (position === 'right' && isLtr) ||
      (position === 'left' && !isLtr)
    ) {
      overlayPosition = { overlayX: 'start', overlayY: 'center' };
    } else {
      throw getOuiTooltipInvalidPositionError(position);
    }

    const { x, y } = this._invertPosition(
      overlayPosition.overlayX,
      overlayPosition.overlayY
    );

    return {
      main: overlayPosition,
      fallback: { overlayX: x, overlayY: y },
    };
  }

  /** Updates the tooltip message and repositions the overlay according to the new message length */
  private _updateTooltipMessage() {
    // Must wait for the message to be painted to the tooltip so that the overlay can properly
    // calculate the correct positioning based on the size of the text.
    if (this._tooltipInstance) {
      this._tooltipInstance.message = this.message;
      this._tooltipInstance._markForCheck();

      this._ngZone.onMicrotaskEmpty
        .asObservable()
        .pipe(take(1), takeUntil(this._destroyed))
        .subscribe(() => {
          if (this._tooltipInstance) {
            this._overlayRef!.updatePosition();
          }
        });
    }
  }

  /** Updates the tooltip class */
  private _setTooltipClass(
    tooltipClass: string | string[] | Set<string> | { [key: string]: any }
  ) {
    if (this._tooltipInstance) {
      this._tooltipInstance.tooltipClass = tooltipClass;
      this._tooltipInstance._markForCheck();
    }
  }

  /** Inverts an overlay position. */
  private _invertPosition(
    x: HorizontalConnectionPos,
    y: VerticalConnectionPos
  ) {
    if (this.position === 'above' || this.position === 'below') {
      if (y === 'top') {
        y = 'bottom';
      } else if (y === 'bottom') {
        y = 'top';
      }
    } else {
      if (x === 'end') {
        x = 'start';
      } else if (x === 'start') {
        x = 'end';
      }
    }

    return { x, y };
  }
}

import { coerceBooleanProperty } from '@angular/cdk/coercion';
import {
  Directive,
  EventEmitter,
  Input,
  isDevMode,
  OnChanges,
  OnDestroy,
  OnInit,
  Output,
} from '@angular/core';
import {
  CanDisable,
  CanDisableCtor,
  HasInitialized,
  HasInitializedCtor,
  mixinDisabled,
  mixinInitialized,
} from '../core';
import { Subject } from 'rxjs';
import { SortDirection } from './sort-direction';
import {
  getSortDuplicateSortableIdError,
  getSortHeaderMissingIdError,
  getSortInvalidDirectionError,
} from './sort-errors';

/** Interface for a directive that holds sorting state consumed by `OuiSortHeader`. */
export interface OuiSortable {
  /** The id of the column being sorted. */
  id: string;

  /** Starting sort direction. */
  start: 'asc' | 'desc';

  /** Whether to disable clearing the sorting state. */
  disableClear: boolean;
}

/** The current sort state. */
export interface Sort {
  /** The id of the column being sorted. */
  active: string;

  /** The sort direction. */
  direction: SortDirection;
}

// Boilerplate for applying mixins to OuiSort.
/** @docs-private */
export class OuiSortBase {}
export const _OuiSortMixinBase: HasInitializedCtor &
  CanDisableCtor &
  typeof OuiSortBase = mixinInitialized(mixinDisabled(OuiSortBase));

/** Container for OuiSortables to manage the sort state and provide default sort parameters. */
@Directive({
  selector: '[ouiSort]',
  exportAs: 'ouiSort',
  // eslint-disable-next-line @angular-eslint/no-inputs-metadata-property
  inputs: ['disabled: ouiSortDisabled'],
})
export class OuiSort
  extends _OuiSortMixinBase
  implements CanDisable, HasInitialized, OnChanges, OnDestroy, OnInit
{
  /** Collection of all registered sortables that this directive manages. */
  sortables = new Map<string, OuiSortable>();

  /** Used to notify any child components listening to state changes. */
  readonly _stateChanges = new Subject<void>();

  /** The id of the most recently sorted OuiSortable. */
  // eslint-disable-next-line @angular-eslint/no-input-rename
  @Input('ouiSortActive') active: string;

  /**
   * The direction to set when an OuiSortable is initially sorted.
   * May be overriden by the OuiSortable's sort start.
   */
  // eslint-disable-next-line @angular-eslint/no-input-rename
  @Input('ouiSortStart') start: 'asc' | 'desc' = 'asc';

  /** The sort direction of the currently active OuiSortable. */
  @Input('ouiSortDirection')
  get direction(): SortDirection {
    return this._direction;
  }
  set direction(direction: SortDirection) {
    if (
      isDevMode() &&
      direction &&
      direction !== 'asc' &&
      direction !== 'desc'
    ) {
      throw getSortInvalidDirectionError(direction);
    }
    this._direction = direction;
  }
  private _direction: SortDirection = '';

  /**
   * Whether to disable the user from clearing the sort by finishing the sort direction cycle.
   * May be overriden by the OuiSortable's disable clear input.
   */
  @Input('ouiSortDisableClear')
  get disableClear(): boolean {
    return this._disableClear;
  }
  set disableClear(v: boolean) {
    this._disableClear = coerceBooleanProperty(v);
  }
  private _disableClear: boolean;

  /** Event emitted when the user changes either the active sort or sort direction. */
  // eslint-disable-next-line @angular-eslint/no-output-rename
  @Output('ouiSortChange')
  readonly sortChange: EventEmitter<Sort> = new EventEmitter<Sort>();

  /**
   * Register function to be used by the contained OuiSortables. Adds the OuiSortable to the
   * collection of OuiSortables.
   */
  register(sortable: OuiSortable): void {
    if (!sortable.id) {
      throw getSortHeaderMissingIdError();
    }

    if (this.sortables.has(sortable.id)) {
      throw getSortDuplicateSortableIdError(sortable.id);
    }
    this.sortables.set(sortable.id, sortable);
  }

  /**
   * Unregister function to be used by the contained OuiSortables. Removes the OuiSortable from the
   * collection of contained OuiSortables.
   */
  deregister(sortable: OuiSortable): void {
    this.sortables.delete(sortable.id);
  }

  /** Sets the active sort id and determines the new sort direction. */
  sort(sortable: OuiSortable): void {
    if (this.active !== sortable.id) {
      this.active = sortable.id;
      this.direction = sortable.start ? sortable.start : this.start;
    } else {
      this.direction = this.getNextSortDirection(sortable);
    }

    this.sortChange.emit({ active: this.active, direction: this.direction });
  }

  /** Returns the next sort direction of the active sortable, checking for potential overrides. */
  getNextSortDirection(sortable: OuiSortable): SortDirection {
    if (!sortable) {
      return '';
    }

    const sortDirectionCycle = getSortDirectionCycle(
      sortable.start || this.start
    );

    // Get and return the next direction in the cycle
    let nextDirectionIndex = sortDirectionCycle.indexOf(this.direction) + 1;
    if (nextDirectionIndex >= sortDirectionCycle.length) {
      nextDirectionIndex = 0;
    }
    return sortDirectionCycle[nextDirectionIndex];
  }

  ngOnInit() {
    this._markInitialized();
  }

  ngOnChanges() {
    this._stateChanges.next();
  }

  ngOnDestroy() {
    this._stateChanges.complete();
  }
}

/** Returns the sort direction cycle to use given the provided parameters of order and clear. */
function getSortDirectionCycle(start: 'asc' | 'desc'): SortDirection[] {
  const sortOrder: SortDirection[] = ['asc', 'desc'];
  if (start === 'desc') {
    sortOrder.reverse();
  }

  return sortOrder;
}

import { Input, Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { UntypedFormControl, UntypedFormBuilder, UntypedFormGroup } from '@angular/forms';
import { startWith, map } from 'rxjs/operators';

export interface StateGroup {
  letter: string;
  names: string[];
}
export const _filter = (opt: string[], value: string): string[] => {
  const filterValue = value.toLowerCase();

  return opt.filter((item) => item.toLowerCase().indexOf(filterValue) === 0);
};

@Component({
  selector: 'oui-autocomplete-storybook',
  template: `
    <oui-form-field
      [appearance]="appearance"
      style="max-width:300px;display:block;"
    >
      <input [formControl]="myControl" oui-input [ouiAutocomplete]="auto" />
    </oui-form-field>
    <oui-autocomplete
      (closed)="closed()"
      (opened)="opened()"
      (click)="optionSelected($event)"
      [autoActiveFirstOption]="autoActiveFirstOption"
      #auto="ouiAutocomplete"
    >
      <oui-option
        *ngFor="let option of filteredOptions | async"
        [value]="option"
      >
        {{ option }}
      </oui-option>
    </oui-autocomplete>
  `,
})
export class OuiAutocompleteStorybook implements OnInit {
  filteredOptions: Observable<any[]>;
  myControl = new UntypedFormControl();
  @Input() options: any[];
  @Input()
  set disabled(value: boolean) {
    if (value) {
      this.myControl.disable();
    } else {
      this.myControl.enable();
    }
  }
  ngOnInit() {
    this.filteredOptions = this.myControl.valueChanges.pipe(
      startWith<string>(''),
      map((value) => (typeof value === 'string' ? value : value)),
      map((option) => (option ? this._filter(option) : this.options.slice()))
    );
  }
  private _filter(option): string[] {
    const filterValue = option.toLowerCase();

    return this.options.filter(
      // eslint-disable-next-line no-shadow
      (option) => option.toLowerCase().indexOf(filterValue) === 0
    );
  }
}

@Component({
  selector: 'oui-autocomplete-group-storybook',
  template: `
    <oui-form-field
      [formGroup]="stateForm"
      [appearance]="appearance"
      style="max-width:300px;display:block;"
    >
      <input
        type="text"
        formControlName="stateGroup"
        oui-input
        [ouiAutocomplete]="autoGroup"
      />
      <oui-autocomplete
        (closed)="closed()"
        (opened)="opened()"
        (click)="optionSelected($event)"
        #autoGroup="ouiAutocomplete"
        class="autocomplete-group"
        [autoActiveFirstOption]="autoActiveFirstOption"
      >
        <oui-optgroup
          *ngFor="let group of stateGroupOptions | async"
          [label]="group.letter"
        >
          <oui-option *ngFor="let name of group.names" [value]="name">
            {{ name }}
          </oui-option>
        </oui-optgroup>
      </oui-autocomplete>
    </oui-form-field>
  `,
})
export class OuiAutocompleteGroupStorybook implements OnInit {
  @Input() stateGroups: StateGroup[];
  @Input()
  set disabled(value: boolean) {
    if (value) {
      this.stateForm.get('stateGroup')!.disable();
    } else {
      this.stateForm.get('stateGroup')!.enable();
    }
  }
  stateForm: UntypedFormGroup = this.fb.group({
    stateGroup: '',
  });
  stateGroupOptions: Observable<StateGroup[]>;

  constructor(private fb: UntypedFormBuilder) {}
  ngOnInit() {
    console.log(this.stateGroups);
    this.stateGroupOptions = this.stateForm
      .get('stateGroup')!
      .valueChanges.pipe(
        startWith(''),
        map((value: string) => this._filterGroup(value))
      );
  }

  private _filterGroup(value: string): StateGroup[] {
    if (value) {
      return this.stateGroups
        .map((group) => ({
          letter: group.letter,
          names: _filter(group.names, value),
        }))
        .filter((group) => group.names.length > 0);
    }

    return this.stateGroups;
  }
}

