.oui-form-field-appearance-legacy {
  .oui-form-field-prefix,
  .oui-form-field-suffix {
    .oui-datepicker-toggle-default-icon {
      width: 1em;
    }
  }
}

.oui-form-field:not(.oui-form-field-appearance-legacy) {
  .oui-form-field-prefix,
  .oui-form-field-suffix {
    .oui-datepicker-toggle-default-icon {
      display: block;
      width: 1.5em;
      height: 1.5em;
    }

    .oui-icon-button .oui-datepicker-toggle-default-icon {
      margin: auto;
    }
  }
}
.oui-datepicker-toggle .oui-icon-button {
  position: absolute;
  padding: 0px;
  width: 100%;
  height: 100%;
  left: 0px;
  top: 0px;
  &:hover {
    background: none;
  }
  .oui-datepicker-toggle-default-icon {
    margin-left: auto;
    margin-top: 4px;
    margin-right: 7px;
    color: #4a4a4a;
  }
}

.oui-datepicker-toggle .oui-icon-button {
  &:hover {
    background: none !important;
  }
}

.oui-calendar-next-button {
  margin-right: 5px;
}

