import { FocusableOption, FocusMonitor, FocusOrigin } from '@angular/cdk/a11y';
import {
  ChangeDetectionStrategy,
  Component,
  ElementRef,
  OnDestroy,
  ViewEncapsulation,
  Inject,
  Optional,
  Input,
} from '@angular/core';
import { CanDisable, CanDisableCtor, mixinDisabled } from '../core';
import { Subject } from 'rxjs';
import { DOCUMENT } from '@angular/common';
import { OUI_MENU_PANEL, OuiMenuPanel } from './menu-panel';

// Boilerplate for applying mixins to OuiMenuItem.

export class OuiMenuItemBase {}
export const _OuiMenuItemMixinBase: CanDisableCtor & typeof OuiMenuItemBase =
  mixinDisabled(OuiMenuItemBase);

/**
 * This directive is intended to be used inside an oui-menu tag.
 * It exists mostly to set the role attribute.
 */
@Component({
  // eslint-disable-next-line @angular-eslint/component-selector
  selector: `[oui-menu-item]`,
  exportAs: 'ouiMenuItem',
  // eslint-disable-next-line @angular-eslint/no-inputs-metadata-property
  inputs: ['disabled'],
  // eslint-disable-next-line @angular-eslint/no-host-metadata-property
  host: {
    '[attr.role]': 'role',
    class: 'oui-menu-item',
    '[class.oui-menu-item-highlighted]': '_highlighted',
    '[class.oui-menu-item-submenu-trigger]': '_triggersSubmenu',
    '[attr.tabindex]': '_getTabIndex()',
    '[attr.aria-disabled]': 'disabled.toString()',
    '[attr.disabled]': 'disabled || null',
    '(click)': '_checkDisabled($event)',
    '(mouseenter)': '_handleMouseEnter()',
  },
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None,
  templateUrl: 'menu-item.html',
})
export class OuiMenuItem
  extends _OuiMenuItemMixinBase
  implements FocusableOption, CanDisable, OnDestroy
{
  /** ARIA role for the menu item. */
  @Input()
  role: 'menuitem' | 'menuitemradio' | 'menuitemcheckbox' = 'menuitem';

  private _document: Document;

  /** Stream that emits when the menu item is hovered. */
  readonly _hovered: Subject<OuiMenuItem> = new Subject<OuiMenuItem>();

  /** Whether the menu item is highlighted. */
  _highlighted = false;

  /** Whether the menu item acts as a trigger for a sub-menu. */
  _triggersSubmenu = false;

  constructor(
    private _elementRef: ElementRef<HTMLElement>,
    @Inject(DOCUMENT) document?: any,
    private _focusMonitor?: FocusMonitor,
    @Inject(OUI_MENU_PANEL)
    @Optional()
    private _parentMenu?: OuiMenuPanel<OuiMenuItem>
  ) {
    // @breaking-change 8.0.0 make `_focusMonitor` and `document` required params.
    super();

    if (_focusMonitor) {
      // Start monitoring the element so it gets the appropriate focused classes. We want
      // to show the focus style for menu items only when the focus was not caused by a
      // mouse or touch interaction.
      _focusMonitor.monitor(this._elementRef.nativeElement, false);
    }

    if (_parentMenu && _parentMenu.addItem) {
      _parentMenu.addItem(this);
    }

    this._document = document;
  }

  /** Focuses the menu item. */
  focus(origin: FocusOrigin = 'program'): void {
    if (this._focusMonitor) {
      this._focusMonitor.focusVia(this._getHostElement(), origin);
    } else {
      this._getHostElement().focus();
    }
  }

  ngOnDestroy() {
    if (this._focusMonitor) {
      this._focusMonitor.stopMonitoring(this._elementRef.nativeElement);
    }

    if (this._parentMenu && this._parentMenu.removeItem) {
      this._parentMenu.removeItem(this);
    }

    this._hovered.complete();
  }

  /** Used to set the `tabindex`. */
  _getTabIndex(): string {
    return this.disabled ? '-1' : '0';
  }

  /** Returns the host DOM element. */
  _getHostElement(): HTMLElement {
    return this._elementRef.nativeElement;
  }

  /** Prevents the default element actions if it is disabled. */
  _checkDisabled(event: Event): void {
    if (this.disabled) {
      event.preventDefault();
      event.stopPropagation();
    }
  }

  /** Emits to the hover stream. */
  _handleMouseEnter() {
    this._hovered.next(this);
  }

  /** Gets the label to be used when determining whether the option should be focused. */
  getLabel(): string {
    const element: HTMLElement = this._elementRef.nativeElement;
    const textNodeType = this._document ? this._document.TEXT_NODE : 3;
    let output = '';

    if (element.childNodes) {
      const length = element.childNodes.length;

      // Go through all the top-level text nodes and extract their text.
      // We skip anything that's not a text node to prevent the text from
      // being thrown off by something like an icon.
      for (let i = 0; i < length; i++) {
        if (element.childNodes[i].nodeType === textNodeType) {
          output += element.childNodes[i].textContent;
        }
      }
    }

    return output.trim();
  }
}

import { OuiIconRegistry } from '../../components';
import { Component, Input, Output, EventEmitter } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';

@Component({
  selector: 'oui-menu-storybook',
  template: `
    <div style="display:inline-block">
      <oui-menu-icon
        [ouiMenuTriggerFor]="afterAboveMenu"
        (menuOpened)="opened()"
        (menuClosed)="closed($event)"
        [vertical]="vertical"
      >
      </oui-menu-icon>
    </div>
    <oui-menu
      #afterAboveMenu
      [hasBackdrop]="hasBackdrop"
      [xPosition]="xPosition"
      (closed)="closed($event)"
      [yPosition]="yPosition"
    >
      <button oui-menu-item>
        <oui-icon svgIcon="edit"></oui-icon>
        <span>Edit</span>
      </button>
      <button oui-menu-item>
        <oui-icon svgIcon="calendar"></oui-icon>
        <span>Schedule</span>
      </button>
      <button oui-menu-item>
        <oui-icon svgIcon="configuration"></oui-icon>
        <span>Configuration</span>
      </button>
    </oui-menu>
  `,
})
export class OuiMenuStorybook {
  @Input() xPosition = 'before';
  @Input() yPosition = 'above';
  @Input() vertical = false;
  @Input() hasBackdrop = true;

  @Output()
  readonly _closed: EventEmitter<string> = new EventEmitter<string>();
  @Output()
  readonly _opened: EventEmitter<void> = new EventEmitter<void>();
  constructor(
    private ouiIconRegistry: OuiIconRegistry,
    private domSanitizer: DomSanitizer
  ) {
    this.ouiIconRegistry.addSvgIcon(
      `local`,
      this.domSanitizer.bypassSecurityTrustResourceUrl(
        `/assets/images/v-green.svg`
      )
    );

    this.ouiIconRegistry.addSvgIconSet(
      this.domSanitizer.bypassSecurityTrustResourceUrl(
        'https://d1azc1qln24ryf.cloudfront.net/135790/oncehub-20/symbol-defs.svg?hn1bl5ss'
      )
    );
  }
  opened() {
    this._opened.emit();
  }
  closed(e: string) {
    this._closed.emit(e);
  }
}

@Component({
  selector: 'oui-nested-menu-storybook',
  template: `
    <div style="display:inline-block">
      <oui-menu-icon
        [ouiMenuTriggerFor]="rootMenu"
        (menuOpened)="opened()"
        (menuClosed)="closed()"
        [vertical]="vertical"
      >
      </oui-menu-icon>
    </div>
    <oui-menu
      [xPosition]="xPosition"
      [hasBackdrop]="hasBackdrop"
      [yPosition]="yPosition"
      (closed)="closed($event)"
      #rootMenu="ouiMenu"
    >
      <button oui-menu-item [ouiMenuTriggerFor]="subMenu">Power</button>
      <button oui-menu-item [ouiMenuTriggerFor]="subMenu">
        System settings
      </button>
    </oui-menu>

    <oui-menu (closed)="closed($event)" #subMenu="ouiMenu">
      <button oui-menu-item>Shut down</button>
      <button oui-menu-item>Restart</button>
      <button oui-menu-item>Hibernate</button>
    </oui-menu>
  `,
})
export class OuiNestedMenuStorybook {
  @Input() xPosition = 'before';
  @Input() yPosition = 'above';
  @Input() vertical = false;
  @Input() hasBackdrop = true;
  @Output()
  readonly _closed: EventEmitter<string> = new EventEmitter<string>();
  @Output()
  readonly _opened: EventEmitter<void> = new EventEmitter<void>();
  constructor(
    private ouiIconRegistry: OuiIconRegistry,
    private domSanitizer: DomSanitizer
  ) {
    this.ouiIconRegistry.addSvgIcon(
      `local`,
      this.domSanitizer.bypassSecurityTrustResourceUrl(
        `/assets/images/v-green.svg`
      )
    );

    this.ouiIconRegistry.addSvgIconSet(
      this.domSanitizer.bypassSecurityTrustResourceUrl(
        'https://d1azc1qln24ryf.cloudfront.net/135790/oncehub-20/symbol-defs.svg?hn1bl5'
      )
    );
  }
  opened() {
    this._opened.emit();
  }
  closed(e: string) {
    this._closed.emit(e);
  }
}

import {
  DOWN_ARROW,
  END,
  ENTER,
  HOME,
  LEFT_ARROW,
  PAGE_DOWN,
  PAGE_UP,
  RIGHT_ARROW,
  UP_ARROW,
  SPACE,
} from '@angular/cdk/keycodes';
import {
  AfterContentInit,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  EventEmitter,
  Inject,
  Input,
  Optional,
  Output,
  ViewEncapsulation,
  ViewChild,
} from '@angular/core';
import { Directionality } from '@angular/cdk/bidi';
import {
  OuiCalendarBody,
  OuiCalendarCell,
