ui/src/components/datepicker/datepicker-.defaultValue:
{
  /** Transforms the height of the datepicker's calendar. */
  transformPanel: trigger('transformPanel', [
    state(
      'void',
      style({
        opacity: 0,
        transform: 'scale(1, 0.8)',
      })
    ),
    transition(
      'void => enter',
      animate(
        '120ms cubic-bezier(0, 0, 0.2, 1)',
        style({
          opacity: 1,
          transform: 'scale(1, 1)',
        })
      )
    ),
    transition('* => void', animate('100ms linear', style({ opacity: 0 }))),
  ]),

  /** Fades in the content of the calendar. */
  fadeInCalendar: trigger('fadeInCalendar', [
    state('void', style({ opacity: 0 })),
    state('enter', style({ opacity: 1 })),

    // TODO(crisbeto): this animation should be removed since it isn't quite on spec, but we
    // need to keep it until #12440 gets in, otherwise the exit animation will look glitchy.
    transition(
      'void => *',
      animate('120ms 100ms cubic-bezier(0.55, 0, 0.55, 0.2)')
    ),
  ]),
}

ui/src/components/sort/sort-animations.t.defaultValue:
{
  /** Animation that moves the sort indicator. */
  indicator: trigger('indicator', [
    state('active-asc', style({ transform: 'rotateX(0deg)' })),
    state('active-desc', style({ transform: 'rotateX(180deg)' })),
    transition(
      'active-asc <=> active-desc',
      animate(SORT_ANIMATION_TRANSITION)
    ),
  ]),

  /** Animation that rotates the left pointer of the indicator based on the sorting direction. */
  leftPointer: trigger('leftPointer', [
    state('active-asc, asc', style({ transform: 'rotate(-45deg)' })),
    state('active-desc, desc', style({ transform: 'rotate(45deg)' })),
    transition(
      'active-asc <=> active-desc',
      animate(SORT_ANIMATION_TRANSITION)
    ),
  ]),

  /** Animation that rotates the right pointer of the indicator based on the sorting direction. */
  rightPointer: trigger('rightPointer', [
    state('active-asc, asc', style({ transform: 'rotate(45deg)' })),
    state('active-desc, desc', style({ transform: 'rotate(-45deg)' })),
    transition(
      'active-asc <=> active-desc',
      animate(SORT_ANIMATION_TRANSITION)
    ),
  ]),

  /** Animation that controls the arrow opacity. */
  arrowOpacity: trigger('arrowOpacity', [
    state('desc-to-active, asc-to-active, active', style({ opacity: 1 })),
    state('desc-to-hint, asc-to-hint, hint', style({ opacity: 0.54 })),
    state(
      'hint-to-desc, active-to-desc, desc, hint-to-asc, active-to-asc, asc, void',
      style({ opacity: 0 })
    ),
    // Transition between all states except for immediate transitions
    transition(
      '* => asc, * => desc, * => active, * => hint, * => void',
      animate('0ms')
    ),
    transition('* <=> *', animate(SORT_ANIMATION_TRANSITION)),
  ]),

  /**
   * Animation for the translation of the arrow as a whole. States are separated into two
   * groups: ones with animations and others that are immediate. Immediate states are asc, desc,
   * peek, and active. The other states define a specific animation (source-to-destination)
   * and are determined as a function of their prev user-perceived state and what the next state
   * should be.
   */
  arrowPosition: trigger('arrowPosition', [
    // Hidden Above => Hint Center
    transition(
      '* => desc-to-hint, * => desc-to-active',
      animate(
        SORT_ANIMATION_TRANSITION,
        keyframes([
          style({ transform: 'translateY(-25%)' }),
          style({ transform: 'translateY(0)' }),
        ])
      )
    ),
    // Hint Center => Hidden Below
    transition(
      '* => hint-to-desc, * => active-to-desc',
      animate(
        SORT_ANIMATION_TRANSITION,
        keyframes([
          style({ transform: 'translateY(0)' }),
          style({ transform: 'translateY(25%)' }),
        ])
      )
    ),
    // Hidden Below => Hint Center
    transition(
      '* => asc-to-hint, * => asc-to-active',
      animate(
        SORT_ANIMATION_TRANSITION,
        keyframes([
          style({ transform: 'translateY(25%)' }),
          style({ transform: 'translateY(0)' }),
        ])
      )
    ),
    // Hint Center => Hidden Above
    transition(
      '* => hint-to-asc, * => active-to-asc',
      animate(
        SORT_ANIMATION_TRANSITION,
        keyframes([
          style({ transform: 'translateY(0)' }),
          style({ transform: 'translateY(-25%)' }),
        ])
      )
    ),
    state(
      'desc-to-hint, asc-to-hint, hint, desc-to-active, asc-to-active, active',
      style({ transform: 'translateY(0)' })
    ),
    state(
      'hint-to-desc, active-to-desc, desc',
      style({ transform: 'translateY(-25%)' })
    ),
    state(
      'hint-to-asc, active-to-asc, asc',
      style({ transform: 'translateY(25%)' })
    ),
  ]),

  /** Necessary trigger that calls animate on children animations. */
  allowChildren: trigger('allowChildren', [
    transition('* <=> *', [query('@*', animateChild(), { optional: true })]),
  ]),
}

ui/src/components/tooltip/tooltip-animat.defaultValue:
{
  /** Animation that transitions a tooltip in and out. */
  tooltipState: trigger('state', [
    state(
      'initial, void, hidden',
      style({ opacity: 0, transform: 'scale(0)' })
    ),
    state('visible', style({ transform: 'scale(1)' })),
    transition(
      '* => visible',
      animate(
        '200ms cubic-bezier(0, 0, 0.2, 1)',
        keyframes([
          style({ opacity: 0, transform: 'scale(0)', offset: 0 }),
          style({ opacity: 0.5, transform: 'scale(0.99)', offset: 0.5 }),
          style({ opacity: 1, transform: 'scale(1)', offset: 1 }),
        ])
      )
    ),
    transition(
      '* => hidden',
      animate('0ms cubic-bezier(0, 0, 0.2, 1)', style({ opacity: 0 }))
    ),
  ]),
}
